import React from 'react';
import { Routes, Route, Navigate, Outlet } from 'react-router-dom';
import { Container, CssBaseline } from '@mui/material';
import Login from './pages/Login';
import Allergies from './pages/Allergies';
import Profile from './pages/Profile';
import Result from './pages/Result';
import Scan from './pages/Scan';
import { useAuth } from './contexts/AuthContext'; // ðŸ‘ˆ useAuth hook'u eklendi

// ... MainLayout bileÅŸeni aynÄ± kalabilir ...
// ... (MainLayout kodunu tekrar buraya yazmanÄ±za gerek yok) ...

// KorumalÄ± Rota BileÅŸeni
const ProtectedRoute = () => {
    const { user } = useAuth(); // ðŸ‘ˆ Oturum bilgisini context'ten alÄ±yoruz

    // EÄŸer kullanÄ±cÄ± oturum aÃ§mÄ±ÅŸsa MainLayout'u gÃ¶ster, yoksa Login sayfasÄ±na yÃ¶nlendir
    return user ? <MainLayout><Outlet /></MainLayout> : <Navigate to="/login" replace />;
};


function App() {
    return (
        <Routes>

            {/* Kimlik DoÄŸrulama SayfalarÄ± */}
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<Login />} />

            {/* KorumalÄ± Sayfalar (Login zorunlu) */}
            <Route element={<ProtectedRoute />}>
                <Route path="/" element={<Scan />} />
                <Route path="/scan" element={<Scan />} />
                <Route path="/allergies" element={<Allergies />} />
                <Route path="/profile" element={<Profile />} />
                {/* SonuÃ§ sayfasÄ±nÄ± korumalÄ± yapÄ±yoruz */}
                <Route path="/result/:barkod" element={<Result />} />
            </Route>

            {/* YanlÄ±ÅŸ URL'ler iÃ§in YÃ¶nlendirme */}
            <Route path="*" element={<Navigate to="/" />} />

        </Routes>
    );
}

export default App;
