import React from 'react';
import { Routes, Route, Navigate, Outlet } from 'react-router-dom';
import { Container, CssBaseline } from '@mui/material';
import Login from './pages/Login';
// ... diÄŸer sayfa importlarÄ± ...
import { useAuth } from './contexts/AuthContext';

// ðŸ‘ˆ MainLayout TANIMI BURADA OLMALIDIR (App fonksiyonunun dÄ±ÅŸÄ±nda)

const MainLayout = ({ children }) => (
    // Bu, tÃ¼m korumalÄ± sayfalarÄ± saran mobil uyumlu ana dÃ¼zendir.
    <Container component="main" maxWidth="sm" sx={{ p: 2, height: '100vh', bgcolor: '#f5f5f5' }}>
        <CssBaseline />
        {children}
    </Container>
);
// ðŸ‘† TanÄ±m bitti.

// KorumalÄ± Rota BileÅŸeni
const ProtectedRoute = () => {
    const { user } = useAuth();

    // EÄŸer kullanÄ±cÄ± oturum aÃ§mÄ±ÅŸsa MainLayout'u ve alt rotalarÄ± (Outlet) gÃ¶ster
    return user ? <MainLayout><Outlet /></MainLayout> : <Navigate to="/login" replace />;
};


function App() { // App fonksiyonu baÅŸlÄ±yor
    return (
        <Routes>

            {/* Kimlik DoÄŸrulama SayfalarÄ± */}
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<Login />} />

            {/* KorumalÄ± Sayfalar, ProtectedRoute iÃ§inde MainLayout kullanÄ±r */}
            <Route element={<ProtectedRoute />}>
                {/* ... rotalarÄ±nÄ±z ... */}
            </Route>

            {/* YanlÄ±ÅŸ URL'ler iÃ§in YÃ¶nlendirme */}
            <Route path="*" element={<Navigate to="/" />} />

        </Routes>
    );
}

export default App;
