import React from 'react';
import { Routes, Route, Navigate, Outlet, useLocation } from 'react-router-dom';
import { Container, CssBaseline, Paper, BottomNavigation, BottomNavigationAction, Box } from '@mui/material';
// ðŸ‘† Box bileÅŸeni eklendiimport QrCodeScannerIcon from '@mui/icons-material/QrCodeScanner';
import FavoriteIcon from '@mui/icons-material/Favorite';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';

// Sayfa importlarÄ±
import Login from './pages/Login';
import Allergies from './pages/Allergies';
import Profile from './pages/Profile';
import Result from './pages/Result';
import Scan from './pages/Scan';

import { useAuth } from './contexts/AuthContext';
import { Link } from 'react-router-dom';

// ----------------------------------------------------------------------
// MOBÄ°L ALT NAVÄ°GASYON BÄ°LEÅžENÄ°
// ----------------------------------------------------------------------
const BottomNav = () => {
    const location = useLocation();
    // Mevcut yolu belirleyip BottomNav'Ä± gÃ¼ncel tutmak iÃ§in
    const getPathValue = (path) => {
        if (path.startsWith('/allergies')) return '/allergies';
        if (path.startsWith('/profile')) return '/profile';
        return '/scan';
    };
    const [value, setValue] = React.useState(getPathValue(location.pathname));

    React.useEffect(() => {
        setValue(getPathValue(location.pathname));
    }, [location.pathname]);

    return (
        <Paper sx={{ position: 'fixed', bottom: 0, left: 0, right: 0, maxWidth: 'sm', margin: '0 auto' }} elevation={3}>
            <BottomNavigation
                showLabels
                value={value}
                onChange={(event, newValue) => {
                    setValue(newValue);
                }}
            >
                <BottomNavigationAction component={Link} to="/scan" label="Tara" value="/scan" icon={<QrCodeScannerIcon />} />
                <BottomNavigationAction component={Link} to="/allergies" label="Alerjilerim" value="/allergies" icon={<FavoriteIcon />} />
                <BottomNavigationAction component={Link} to="/profile" label="Profil" value="/profile" icon={<AccountCircleIcon />} />
            </BottomNavigation>
        </Paper>
    );
};
// ----------------------------------------------------------------------


// ----------------------------------------------------------------------
// ANA DÃœZEN (LAYOUT) BÄ°LEÅžENÄ°
// ----------------------------------------------------------------------
const MainLayout = ({ children }) => (
    // Container, iÃ§eriÄŸi mobil cihaz boyutunda tutar (max-width: 600px).
    <Container component="main" maxWidth="sm" sx={{
        p: 0, // Padding'i 0 yaptÄ±k, iÃ§eriÄŸi iÃ§ bileÅŸenler yÃ¶netecek
        minHeight: '100vh',
        bgcolor: '#f5f5f5',
        position: 'relative', // BottomNav iÃ§in
        pb: '56px' // BottomNav yÃ¼ksekliÄŸi kadar boÅŸluk bÄ±rak
    }}>
        <CssBaseline />
        <Box sx={{ p: 2, height: '100%' }}>
            {children}
        </Box>
        <BottomNav />
    </Container>
);
// ----------------------------------------------------------------------


// ----------------------------------------------------------------------
// KORUMALI ROTA (PROTECTED ROUTE) BÄ°LEÅžENÄ°
// ----------------------------------------------------------------------
const ProtectedRoute = () => {
    const { user, loading } = useAuth();

    // YÃ¼kleniyorsa beklemeye devam et
    if (loading) return null; // AuthContext'te de yÃ¼klenme ekranÄ± var, bu fazlalÄ±k olabilir ama gÃ¼venlik iÃ§in iyi.

    // EÄŸer kullanÄ±cÄ± oturum aÃ§mÄ±ÅŸsa MainLayout'u ve alt rotalarÄ± (Outlet) gÃ¶ster
    return user ? <MainLayout><Outlet /></MainLayout> : <Navigate to="/login" replace />;
};


// ----------------------------------------------------------------------
// APP FONKSÄ°YONU
// ----------------------------------------------------------------------
function App() {
    return (
        <Routes>

            {/* 1. Kimlik DoÄŸrulama SayfalarÄ± */}
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<Login />} />

            {/* 2. KorumalÄ± Sayfalar (Login zorunlu) */}
            <Route element={<ProtectedRoute />}>
                <Route path="/" element={<Scan />} />
                <Route path="/scan" element={<Scan />} />
                <Route path="/allergies" element={<Allergies />} />
                <Route path="/profile" element={<Profile />} />
                <Route path="/result/:barkod" element={<Result />} />
            </Route>

            {/* 3. YanlÄ±ÅŸ URL'ler iÃ§in YÃ¶nlendirme */}
            <Route path="*" element={<Navigate to="/" />} />

        </Routes>
    );
}

export default App;
