import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import {
    Container,
    Box,
    Typography,
    TextField,
    Button,
    Alert,
    Link,
    CircularProgress // Y�klenme animasyonu i�in
} from '@mui/material';

// Bu bile�en hem Giri� hem de Kay�t olarak hizmet verecektir
const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [isSigningUp, setIsSigningUp] = useState(false); // Kay�t m�, Giri� mi?
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);

    const { signIn, signUp, user } = useAuth();
    const navigate = useNavigate();

    // E�er kullan�c� zaten giri� yapm��sa, onu ana sayfaya y�nlendir
    if (user) {
        navigate('/', { replace: true });
        return null; // Y�nlendirme yap�l�rken bile�enin render edilmesini durdur
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        try {
            let response;

            if (isSigningUp) {
                // KAYIT OLMA i�lemi
                response = await signUp({ email, password });
            } else {
                // G�R�� YAPMA i�lemi
                response = await signIn({ email, password });
            }

            if (response.error) {
                throw response.error;
            }

            // Ba�ar�l� giri�/kay�t sonras� otomatik olarak ana sayfaya y�nlendirilir
            // ��nk� AuthContext oturum de�i�ikli�ini alg�layacak.

        } catch (err) {
            // Supabase'den gelen hata mesaj�n� g�ster
            setError(err.message || 'Bir hata olu�tu. L�tfen tekrar deneyin.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <Container component="main" maxWidth="xs" sx={{ height: '100vh', display: 'flex', alignItems: 'center' }}>
            <Box
                sx={{
                    width: '100%',
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    p: 3,
                    boxShadow: 3, // Hafif g�lge ekleyerek daha mobil uygulama hissi veriyoruz
                    borderRadius: 2,
                    bgcolor: 'white'
                }}
            >
                <Typography component="h1" variant="h5" sx={{ mb: 2 }}>
                    {isSigningUp ? 'Yeni Hesap Olu�tur' : 'Giri� Yap'}
                </Typography>

                {error && <Alert severity="error" sx={{ width: '100%', mb: 2 }}>{error}</Alert>}

                <Box component="form" onSubmit={handleSubmit} noValidate sx={{ mt: 1, width: '100%' }}>

                    <TextField
                        margin="normal"
                        required
                        fullWidth
                        label="E-Posta Adresi"
                        name="email"
                        autoComplete="email"
                        autoFocus
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        disabled={loading}
                    />

                    <TextField
                        margin="normal"
                        required
                        fullWidth
                        name="password"
                        label="�ifre"
                        type="password"
                        autoComplete="current-password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        disabled={loading}
                    />

                    <Button
                        type="submit"
                        fullWidth
                        variant="contained"
                        sx={{ mt: 3, mb: 2, height: 50 }}
                        disabled={loading || !email || !password}
                    >
                        {loading ? (
                            <CircularProgress size={24} color="inherit" />
                        ) : (
                            isSigningUp ? 'Kay�t Ol' : 'Giri� Yap'
                        )}
                    </Button>

                    {/* Giri�/Kay�t modunu de�i�tiren ba�lant� */}
                    <Box sx={{ textAlign: 'center' }}>
                        <Link
                            href="#"
                            variant="body2"
                            onClick={() => setIsSigningUp(!isSigningUp)}
                        >
                            {isSigningUp ? 'Zaten hesab�n�z var m�? Giri� Yap�n.' : 'Hesab�n�z yok mu? Hemen Kay�t Olun.'}
                        </Link>
                    </Box>
                </Box>
            </Box>
        </Container>
    );
};

export default Login;
