import React from 'react';
import { Typography, Box, Button } from '@mui/material';
import QrCodeScannerIcon from '@mui/icons-material/QrCodeScanner';
import { useNavigate } from 'react-router-dom';

const Scan = () => {
    const navigate = useNavigate();

    // Kamera entegrasyonu daha sonra yapılacak. Şimdilik simülasyon butonu:
    const simulateScan = () => {
        const dummyBarcode = '869000000001'; // Örnek bir barkod
        navigate(`/result/${dummyBarcode}`);
    };

    return (
        <Box sx={{ textAlign: 'center', pt: 5, pb: 2 }}>
            <QrCodeScannerIcon sx={{ fontSize: 80, color: 'primary.main', mb: 2 }} />
            <Typography variant="h5" gutterBottom>Ürün Taraması</Typography>
            <Typography variant="body1" color="text.secondary" sx={{ mb: 4 }}>
                Alerji kontrolü için ürün barkodunu tarayın.
            </Typography>
            <Button
                variant="contained"
                size="large"
                onClick={simulateScan}
                sx={{ width: '100%', maxWidth: 300 }}
            >
                Tarama Başlat (Simülasyon)
            </Button>
        </Box>
    );
};

export default Scan;
